mapper.update:
UPDATE crew 
	SET crew_name = #{crew_name}, member = #{member}, minute = #{minute}, distance = #{distance}, content = #{content}, 
		days = #{days}, address = #{address}, sido = #{sido}, sigungu = #{sigungu}, shortsido = #{shortsido}, is_recruit = #{is_recruit} 
	WHERE crew_idx = #{crew_idx}

mapper.select:
SELECT m.id, m.nickname, m.gender, COALESCE(p.image, '') as image, cm.is_leader, cm.create_date
	FROM crew_member cm JOIN member m ON cm.id = m.id
	LEFT JOIN profile p ON m.id = p.id WHERE cm.crew_idx = #{crew_idx};

SELECT m.id, m.nickname, m.gender, m.birth, COALESCE(p.image, '') as image, ca.create_date, (SELECT COUNT(*) FROM crew_application WHERE crew_idx = #{crew_idx}) AS totalpage FROM crew_application ca JOIN member m ON ca.id = m.id LEFT JOIN profile p ON m.id = p.id WHERE ca.crew_idx = #{crew_idx}
AND m.nickname LIKE CONCAT('%', #{keyword}, '%')
GROUP BY m.id, m.nickname, m.gender, m.birth, p.image
LIMIT #{limit} OFFSET #{offset}
ORDER BY ca.create_date DESC

SELECT m.id AS member_id, m.nickname AS member_nickname, COALESCE(p.image, '') AS member_image,
leader.id AS leader_id, leader.nickname AS leader_nickname, COALESCE(leader_profile.image, '') AS leader_image,
cah.create_date, cah.update_date, cah.is_agree,
(SELECT COUNT(*) FROM crew_application_history WHERE crew_idx = #{crew_idx}) AS totalpage FROM crew_application_history cah JOIN member m ON cah.id = m.id LEFT JOIN profile p ON m.id = p.id JOIN member leader ON cah.leader = leader.id LEFT JOIN profile leader_profile ON leader.id = leader_profile.id WHERE cah.crew_idx = #{crew_idx}
AND m.nickname LIKE CONCAT('%', #{keyword}, '%')
GROUP BY cah.history_idx;
LIMIT #{offset}, #{limit}

mapper.delete:
DELETE FROM crew_member
    WHERE crew_idx = #{crew_idx}
      AND id IN 

          #{id}
