mapper.select:
    	SELECT m.id, b.board_idx, b.subject, b.content, m.nickname, b.create_date , b.bHit ,b.likes,m.icon_image
			FROM board b
		LEFT JOIN member m ON b.id = m.id WHERE b.board_idx = #{param1}

mapper.update:
UPDATE board
SET subject = #{subject},
    content = #{content}
WHERE board_idx = #{board_idx} AND id = #{id}
