mapper.select:

	SELECT m.birth,m.gender,m.id,ib.buy_date
    FROM icon_buy ib
    JOIN member m ON ib.id = m.id
    where ib.icon_idx = #{icon_idx}
    ORDER BY icon_idx DESC LIMIT #{limit} OFFSET #{offset}

mapper.update:
UPDATE member SET point = (
	SELECT SUM(point) FROM member_point 
	WHERE id = #{userId}
) 
	WHERE id = #{userId}
